import '../style.css'
import { useState } from "react";
import google from '../images/GOOGL.png';
import fb from '../images/FB.png'
import amazon from '../images/AMZN.svg'
function Card() {
  const [isDragging, setIsDragging] = useState(false);

  function handleDragStart(e) {
    setIsDragging(true);
    const data = JSON.stringify({ type: "card" });
    e.dataTransfer.setData("text/plain", data);
  }

  function handleDragEnd(e) {
    setIsDragging(false);
    e.dataTransfer.clearData();
  }

  return (
    <div
      className="card"
      style={{
        backgroundColor: isDragging ? "#fbb" : "white"
      }}
      draggable
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
    >
      <div className='heroCard'>
                <div className='icon'><img src={google} alt="" /></div>
                <div className='price'><p>300</p> <p className='currency'>USD</p></div>
                <div>GOOGL</div>
            </div>
    </div>
  );
}

function Box({ card, moveCard,img,price, }) {
  const [isOver, setIsOver] = useState(false);

  function handleDragOver(e) {
    if (e.dataTransfer.types[0] === "text/plain") {
      setIsOver(true);
      e.preventDefault();
    }
  }

  function handleDrop(e) {
    const dataJSON = e.dataTransfer.getData("text/plain");
    let data;
    try {
      data = JSON.parse(dataJSON);
    } catch {}
    if (data && data.type === "card") {
      moveCard();
    }
  }

  function handleDragLeave() {
    setIsOver(false);
  }

  return (
    <div
      className="box"
      style={{ backgroundColor: isOver ? "#bbf" : "rgba(0,0,0,.12)" }}
      onDragOver={handleDragOver}
      onDrop={handleDrop}
      onDragLeave={handleDragLeave}
    >
      {card ? <Card /> : <div className='heroCard'>
                <div className='icon'><img src={fb} alt="" /></div>
                <div className='price'><p>360</p> <p className='currency'>USD</p></div>
                <div>FB</div>
            </div>}
    </div>
  );
}

export default function App() {
  const [index, setIndex] = useState(1);

  function moveCard(i) {
    setIndex(i);
  }

  return (
    <div className="app">
      <Box card={index === 1} moveCard={moveCard.bind(null, 1)}></Box>
      <Box card={index === 2} moveCard={moveCard.bind(null, 2)}></Box>
      <Box card={index === 3} moveCard={moveCard.bind(null, 3)}></Box>
    </div>
  );
}
