import React from 'react'
import MainSkeleton from './MainSkeleton';
import {useEffect,useState} from "react";
import axios from "axios";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';
import TextField from '@mui/material/TextField';
import { Button, Container } from '@mui/material';
const SavedData = () => {
  return (
    <div>
<MainSkeleton/>
<Container maxWidth="lg">
      <TextField
          label="Search For a Crypto Currency.."
          variant="outlined"
          style={{ marginBottom: 20, width: "100%" }}
          onChange={(e) => setSearch(e.target.value)}
        />
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
        <TableRow>
                  {["Coin", "Symbol", "Price", "Action", "Market Cap"].map((head) => (
                    <TableCell
                      style={{
                        color: "black",
                        fontWeight: "700",
                        fontFamily: "Montserrat",
                        minWidth: "120px"
                      }}
                      key={head}
                      align={"left"}
                    >
                      {head}
                    </TableCell>
                  ))}
                </TableRow>
        </TableHead>
        <TableBody>
                {handleSearch()
                  .slice((page - 1) * 10, (page - 1) * 10 + 10)
                  .map((row) => {
                    return (
                      <TableRow

                        key={row.name}
                      >
                        <TableCell
                        align="left"
                          component="th"
                          scope="row"
                          style={{
                            display: "flex",
                            gap: 10,
                          }}
                        >
                          <img
                            src={row?.image}
                            alt={row.name}
                            height="40"
                            style={{ marginBottom: 10 }}
                          />
                          <div
                            style={{ display: "flex", flexDirection: "column" }}
                          >
                            <span
                              style={{
                                textTransform: "uppercase",
                                fontSize: 22,
                              }}
                            >
                              {row.symbol}
                            </span>
                            <span style={{ color: "darkgrey" }}>
                              {row.name}
                            </span>
                          </div>
                        </TableCell>
                        <TableCell align="left">

                          {row.symbol.toUpperCase()}
                        </TableCell>
                        <TableCell align="left">

                          {numberWithCommas(row.current_price.toFixed(2))}
                        </TableCell>
                        <TableCell
                          align="left"
                        >
                          <Button sx={{width:"100px"}} variant="contained">save</Button>
                        </TableCell>
                        <TableCell align="left">
                          {row.market_cap}
                          &nbsp;M
                        </TableCell>
                      </TableRow>
                    );
                  })}
              </TableBody>
      </Table>
    </TableContainer>
    <Stack spacing={2} sx={{padding:"10px",backgroundColor:'white'}} flexDirection="row" justifyContent="center">
      <Pagination style={{justifyContent: "center",width:400,flexDirection:"row"}} count={(handleSearch()?.length / 10).toFixed(0)} page={page} onChange={(event,value)=>setPage(value)} color="primary" />
    </Stack>
    </Container>
    </div>
  )
}

export default SavedData
